package bigcamp.yok.teacher;

public class Avatar {

	String head; 
	String headBack;
	String headFront; 
	
	String torso; 
	String torsoBack;
	String torsoFront; 
	
	String leg;
	String legBack;
	String legFront; 
	
	String wholeBack; 
	
	public Avatar(String head, String headBack, String headFront, String torso, String torsoBack,
			String torsoFront, String leg, String legBack, String legFront, String wholeBack) {
		// TODO Auto-generated constructor stub
		
		this.head = head; 
		this.headBack = headBack;
		this.headFront = headFront;
		
		this.torso = torso; 
		this.torsoBack = torsoBack;
		this.torsoFront = torsoFront; 
		
		this.leg = leg; 
		this.legBack = legBack; 
		this.legFront = legFront; 
		
		this.wholeBack = wholeBack; 
	}
	
	
	
}
